var main=new Vue({el:"#main",data:{currentUser:null,currentUserLoadStatus:"notload",currentUserLoadError:""},methods:{loadUser:function(){var currentUserId=getQueryString("user");null==currentUserId&&(currentUserId=getLastUrlAgrs(location.href).arg);var url=blog_api_address+"user/"+currentUserId;$.ajax({url:url,success:function(response){response.success?(main.currentUserLoadStatus="loaded",main.currentUser=response.data):(main.currentUserLoadStatus="failed",main.currentUserLoadError=response.message)},error:function(xhr,err){main.currentUserLoadStatus="failed",main.currentUserLoadError=err}})},getUserHead:()=>isNullOrEmpty(main.currentUser.headimg)?"/images/default/head-default.png":getImageUrlFormHash(main.currentUser.headimg),getUserCardBackground:()=>isNullOrEmpty(main.currentUser.cardBackground)?"/images/background/mebg.jpg":getImageUrlFormHash(main.currentUser.cardBackground)}});setLoaderFinishCallback(function(){main.loadUser()});